
	//encapsulation: we didn't get or take  a direct value with a help of method is called encaspulation

package com.java;


class Data{
	private int id;
	private String password;
	
	public int getId() {
		return id;
	}
	public Data(int id, String password) {
		super();
		this.id = id;
		this.password = password;
	}
	public void setId(int id) {
		this.id = id;
	}
	public String getPassword() {
		return password;
	}
	public void setPassword(String password) {
		this.password = password;
	}
}
	
	
	
